METHOD.ACTIONSCRIPT:
#PRIVATE() #CALL:CFG_EV_FATURAMENTO MILLENIUM.CONFIGURACOES.LERPARAM ( 'EVL_FATURAMENTO_EVENTO_FRANQUIA', 'I' );
SELECT:MOV V.COD_OPERACAO FROM (SELECT S.SAIDA AS COD_OPERACAO FROM SAIDAS S INNER JOIN EMBARQUE_ITENS EI ON EI.ORIGEM = S.SAIDA AND EI.TIPO_ORIGEM = 'S' INNER JOIN NF N ON N.COD_OPERACAO = S.SAIDA AND N.TIPO_OPERACAO = 'S' INNER JOIN EMBARQUES E ON E.EMBARQUE = EI.EMBARQUE WHERE S.EVENTO = :CFG_EV_FATURAMENTO.VALOR_I AND S.EVL_TRANSFERENCIA_FRANQUIA = 'F' AND S.CANCELADA = 'F' AND N.CANCELADA = 'F' AND N.STATUS = '100' AND E.STATUS = 2 //AND S.ROMANEIO = '132785'
UNION ALL
SELECT S.SAIDA AS COD_OPERACAO FROM SAIDAS S INNER JOIN NF N ON N.COD_OPERACAO = S.SAIDA AND N.TIPO_OPERACAO = 'S' WHERE S.CANCELADA = 'F' AND S.EVENTO = 1000011 AND S.EVL_TRANSFERENCIA_FRANQUIA = 'F' AND S.CLIENTE IN (1049313,1048164,1051299) AND N.status = '100' )V;
